import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { ajax_or_login } from "../../ajax";

function EditProfilePage() {
    return (
      <div>
        <Header />
        <EditProfile />
        <Footer />
      </div>
    );
  }


  function Header() {
    return <div className="header"></div>;
  }


  function Footer() {
    return (
    <footer className="text-center text-lg-start bg-light text-muted fixed-bottom">
    <div
        className="text-center p-4"
        style={{ backgroundColor: 'rgba(0, 0, 0, 0.05)' }}
        >
        © 2023 Copyright
    </div>
    </footer>
);
}


function EditProfile() {
  const navigate = useNavigate();
  const [error, setError] = useState("");
  const [profileData, setProfileData] = useState({
    avatar: '',
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    description: ''
  });



  useEffect(() => {
    ajax_or_login("/accounts/profile/", {}, navigate)
    .then(response => response.json())
    .then(json => {
        let errorMessages = [];
        for (let key in json) {
            if (Array.isArray(json[key])) {
                errorMessages.push(`${key}: ${json[key].join(', ')}`);
            }else if (key === 'error') {
              errorMessages.push(json[key]);
          }
        }
        if (errorMessages.length > 0) {
            setError(errorMessages.join('; '));
        }
        setProfileData(json);})
}, [navigate]);


  const handleChange = (event) => {
    const { name, value } = event.target;
    setProfileData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch('YOUR_BACKEND_API_URL', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(profileData),
      });

      if (response.ok) {
        navigate('/yourprofile/');
      } else {
        console.error('Error updating profile data');
      }
    } catch (error) {
      console.error('Error updating profile data:', error);
    }
  };

  const handleCancel = () => {
    navigate('/yourprofile/');
  };
  const defaultAvatar = 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava3.webp';
  return (
    <div className="row">
      <div className="my-auto">
        <div className="card mb-4 back">
          <div className="card-body text-center">
          <img
                src={profileData.avatar || defaultAvatar}
                alt="avatar"
                className="rounded-circle img-fluid"
                style={{ width: '150px' }}
              />
            <p className="text-muted">Upload new avatar</p>
            <div className="d-flex justify-content-center mb-2">
              <input
                className="form-control form-control-sm"
                id="formFileSm"
                type="file"
                placeholder="Upload new image"
              />
            </div>
          </div>
        </div>

        <div className="my-auto">
          <div className="card mb-4">
            <div className="card-body">
              {/* Add more input fields for each profile data field */}
              <div className="row">
                <div className="col-sm-3">
                  <p className="mb-0 my-3">First Name</p>
                </div>
                <div className="col-sm-9">
                  <input
                    type="text"
                    className="form-control my-3"
                    placeholder="Your First Name"
                    defaultValue={profileData.first_name}
                    name="first_name"
                  />
                </div>
              </div>
              <div className="row">
                <div className="col-sm-3">
                  <p className="mb-0 my-3">Last Name</p>
                </div>
                <div className="col-sm-9">
                  <input
                    type="text"
                    className="form-control my-3"
                    placeholder="Your LastName"
                    defaultValue={profileData.last_name}
                    name="first_name"
                  />
                </div>
              </div>

              <div className="row">
                <div className="col-sm-3">
                  <p className="mb-0 my-3">Username</p>
                </div>
                <div className="col-sm-9">
                  <input
                    type="text"
                    className="form-control my-3"
                    placeholder="Username"
                    defaultValue={profileData.username}
                    name="username"
                  />
                </div>
              </div>

              <div className="row">
                <div className="col-sm-3">
                  <p className="mb-0 my-3">Email</p>
                </div>
                <div className="col-sm-9">
                  <input
                    type="text"
                    className="form-control my-3"
                    placeholder="Username"
                    defaultValue={profileData.email}
                    name="email"
                  />
                </div>
              </div>


              <div className="row">
                <div className="col-sm-3">
                  <p className="mb-0 my-3">Phone Number</p>
                </div>
                <div className="col-sm-9">
                  <input
                    type="text"
                    className="form-control my-3"
                    placeholder="Username"
                    defaultValue={profileData.phone_number}
                    name="phone_number"
                  />
                </div>
              </div>

              <div className="row">
                <div className="col-sm-3">
                  <p className="mb-0 my-3">Password</p>
                </div>
                <div className="col-sm-9">
                  <input
                    type="text"
                    className="form-control my-3"
                    placeholder="Username"
                    defaultValue={profileData.password}
                    name="password"
                  />
                </div>
              </div>

              <div className="row">
                <div className="col-sm-3">
                  <p className="mb-0 my-3">Confirmed Password</p>
                </div>
                <div className="col-sm-9">
                  <input
                    type="text"
                    className="form-control my-3"
                    placeholder="Username"
                    defaultValue={profileData.password_confirmation}
                    name="password_confirm"
                  />
                </div>
              </div>

              <p className="error">{error}</p>
              <div className="d-flex flex-row-reverse">
                <button
                  type="button"
                  id="submit"
                  name="submit"
                  className="btn btn-warning rounded-9 me-2"
                  onClick={handleSubmit}
                >
                  Update
                </button>
                <button
                  type="button"
                  id="submit"
                  name="submit"
                  className="btn btn-secondary me-2 rounded-9"
                  onClick={handleCancel}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EditProfilePage;
