import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import { ajax } from '../../ajax';
import './login.css';



function LoginPage() {
  return (
    <div>
      <Header />
      <MainContent />
      <Footer />
    </div>
  );
}

function Header() {
  return <div className="header"></div>;
}

function MainContent() {
  return (
    <div className="container d-flex flex-column flex-lg-row justify-content-evenly mt-5 pt-5">
      <Heading />
      <LoginForm />
    </div>
  );
}

function Heading() {
  return (
    <div className="text-center text-lg-start mt-lg-5 pt-lg-5">
      <h1 className="text-warning fw-bold fs-lg">Restify</h1>
      <p className="w-75 mx-auto mx-lg-0 fs-4 writer">
        Your home everywhere you go.
      </p>
    </div>
  );
}

function LoginForm() {
const [error, setError] = useState("");
const navigate = useNavigate();
function handle_submit(event) {
    let data = new FormData(event.target);

    ajax("/api/token/", {
        method: "POST",
        body: data,
    })
    .then(request => request.json())
    .then(json => {
        if ('access' in json) {
            localStorage.setItem('access', json.access);
            localStorage.setItem('username', data.get('username'));
            navigate('/');
        }
        else if ('detail' in json) {
            setError(json.detail);
        }
        else {
            setError("Unknown error while signing in.")
        }
    })
    .catch(error => {
        setError(error);
    });

    event.preventDefault();
}


  return (
    <div style={{ maxWidth: '28rem', width: '100%' }}>
      <div className="bg-success-subtle shadow rounded p-3 input-group-lg">
      <form onSubmit={handle_submit}>
      <input
        type="text"
        className="form-control my-3"
        placeholder="Enter your username"
        id="username" name="username"
        required
      />
      <input
        type="password"
        className="form-control my-3"
        placeholder="Password"
        id="password" name="password"
        required
      />
      <button type="submit" className="btn btn-primary w-100 my-3 rounded-9">
        Log In
      </button>
      <p className="error">{error}</p>
    </form>
    <hr />
    <div class="text-center my-4">
          <p class="text-secondary">New to Restify?</p>
          <button class="btn btn-warning btn-lg" data-bs-toggle="modal" data-bs-target="#createModal">Create New Account</button>
    </div>
    {/* <SignUpSection /> */}
  </div>
</div>

);
}


















