import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import { ajax } from '../../ajax';
import './login.css';
import Reservation from '../../components/Reservations';



function ReservationsPage() {
  return (
    <div>
      <Header />
      <MainContent />
      <Footer />
    </div>
  );
}

function Header() {
  return <div className="header"></div>;
}

function MainContent(){
    const navigate = useNavigate();
    const [ reservations, setReservations ] = useState([]);//然后在底下分为两种

    useEffect(() => {
        ajax_or_login("/all/", {}, navigate)
        .then(response => response.json())
        .then(json => setReservations(json))
    }, [navigate]);

    return <>
        <h1>View Tasks</h1>
        //怀疑排版会有问题
        {reservations.map(reservation => <Reservation {...reservation} />)}
    </>;

}

function Footer() {
    return (
    <footer className="text-center text-lg-start bg-light text-muted fixed-bottom">
    <div
        className="text-center p-4"
        style={{ backgroundColor: 'rgba(0, 0, 0, 0.05)' }}
        >
        © 2023 Copyright
    </div>
    </footer>
);
}

export default ReservationsPage;
