import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import { ajax_or_login } from '../../ajax';
import './style.css';
import Reservation from '../../components/Reservations';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faGlobeAsia, faTag } from '@fortawesome/free-solid-svg-icons';
import Pagination from 'react-bootstrap/Pagination';
const [currentPage, setCurrentPage] = useState(1);
const itemsPerPage = 5;

function ReservationsPageC() {
  return (
    <div>
      <Header />
      <MainContent />
      <Footer />
    </div>
  );
}

function Header() {
  return <div className="header"></div>;
}

function MainContent(){
    const navigate = useNavigate();
    const [ reservationsclient, setReservationsclient ] = useState([]);

    useEffect(() => {
        ajax_or_login("/reservations/client/all/", {}, navigate)
          .then(response => response.json())
          .then(json => {
            if (json === null) {
              setReservationsclient([]);
            } else if (Array.isArray(json)) {
              setReservationsclient(json);
            } else {
              console.error('Unexpected data format:', json);
            }
          });
      }, [navigate]);


    // useEffect(() => {
    //     ajax_or_login("/reservations/host/all/", {}, navigate)
    //     .then(response => response.json())
    //     .then(json => setReservationshost(json))
    // }, [navigate]);


      const pendingReservations = filterAndSortReservations(reservationsclient, "pending");
      const confirmedReservations = filterAndSortReservations(reservationsclient, "approved");
      const cancelledReservations = filterAndSortReservations(reservationsclient, "cancelled");
      const deniedReservations = filterAndSortReservations(reservationsclient, "denied");
      const expiredReservations = filterAndSortReservations(reservationsclient, "expired");
      const terminatedReservations = filterAndSortReservations(reservationsclient, "terminated");
      const completedReservations = filterAndSortReservations(reservationsclient, "completed");



    return (
            <div className="container py-5">
              <div className="d-flex justify-content-between">
                <h1>My Reservations</h1>
                <div>
                  <p>Create New Properties!</p>
                  <div className="d-flex justify-content-center">
                    <a href="CreateProperty.html">
                      <button type="button" className="btn btn-warning btn-lg btn-floating">
                        <FontAwesomeIcon icon={faPlus} />
                      </button>
                    </a>
                  </div>
                </div>
              </div>
              <ul className="nav nav-tabs mb-3" id="myTab0" role="tablist">
                <li className="nav-item" role="presentation">
                  <button
                    className="nav-link"
                    id="home1"
                    data-mdb-toggle="tab"
                    data-mdb-target="#home0"
                    type="button"
                    role="tab"
                    aria-controls="home"
                    aria-selected="true"
                  >
                    pending
                  </button>
                </li>
                <li className="nav-item" role="presentation">
                  <button
                    className="nav-link"
                    id="home2"
                    data-mdb-toggle="tab"
                    data-mdb-target="#home0"
                    type="button"
                    role="tab"
                    aria-controls="home2"
                    aria-selected="false"
                  >
                    denied
                  </button>
                </li>
                <li className="nav-item" role="presentation">
                  <button
                    className="nav-link"
                    id="home3"
                    data-mdb-toggle="tab"
                    data-mdb-target="#home0"
                    type="button"
                    role="tab"
                    aria-controls="home3"
                    aria-selected="false"
                  >
                    expired
                  </button>
                </li>
                <li className="nav-item" role="presentation">
                  <button
                    className="nav-link"
                    id="home4"
