function filterAndSortReservations(reservations, status) {
  return reservations
    .filter(reservation => reservation.status === status)
    .sort((a, b) => new Date(a.date) - new Date(b.date))
    .slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);
}


function renderPagination(totalItems, itemsPerPage, currentPage, setCurrentPage) {
  const totalPages = Math.ceil(totalItems / itemsPerPage);
  const items = [];

  for (let number = 1; number <= totalPages; number++) {
    items.push(
      <Pagination.Item key={number} active={number === currentPage} onClick={() => setCurrentPage(number)}>
        {number}
      </Pagination.Item>,
    );
  }

  return (
    <Pagination>
      <Pagination.Prev onClick={() => setCurrentPage(prev => (prev > 1 ? prev - 1 : prev))} />
      {items}
      <Pagination.Next onClick={() => setCurrentPage(prev => (prev < totalPages ? prev + 1 : prev))} />
    </Pagination>
  );
}

