import { useEffect, useState } from 'react';
import './style.css';
import { useNavigate } from 'react-router-dom';
import { ajax_or_login } from '../../ajax';

function Reservation(props) {
    const date = new Date(props.due_date);
    let [state, setState] = useState("");
    let [error, setError] = useState("");
    let navigate = useNavigate();

    useEffect(() => {
        if (props.completed) {
            setState("completed");
        }
        else if (new Date(props.due_date) < new Date()) {
            setState("expired");
        }
        else {
            setState("");
        }
    }, [props]);//颜色

    const handle_complete = () => {
        let data = new FormData();
        data.append("completed", true);
        ajax_or_login(`/task/${props.id}/completed/`, {
            method: "PUT",
            body: data,
        }, navigate)
        .then(response => {
            if (response.ok) {
                props.complete(props.id, true);
            }
            else {
                setError("Unknown error when attempting to set task to completed");
            }
        })
        .catch(err => setError(err.toString()));
    };

    return <details className={state} open={props.open} > //这里css 指的是一个 complete在这个state里 现在的计算机课一般难点在于翻译和convention 所以抓准逻辑 
        <summary>
            <h2>{props.title}</h2>
            <span>{date.toLocaleString()}</span>
        </summary>
        <div>
            <p>{props.detail}</p>
            {(state !== 'completed')
                ? <p><button className="btn" onClick={handle_complete}>Complete</button></p>
                : ''}
            <p className="error">{error}</p>
        </div>
    </details>;
}

export default Task;
